	/*---------------------Serach subscriber student Details ----------------------------*/
    public static final String studentlistdetailsbysubNo="SELECT DISTINCT clsd.*,CASE WHEN clsd.status='block' THEN 'Blocked' ELSE clsd.status END statuss,CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student,st.student_admissionno_var,CASE WHEN csc.student_rollno='' THEN '-' WHEN csc.student_rollno IS NULL THEN '-' ELSE csc.student_rollno END student_rollno,cd.classdetails_name_var,cs.classsection_name_var FROM campus_library_subscriber_details clsd JOIN campus_student st ON clsd.subscriberId=st.student_id_int JOIN campus_student_classdetails csc ON st.student_id_int=csc.student_id_int JOIN campus_classdetail cd ON csc.classdetail_id_int=cd.classdetail_id_int AND csc.locationId=cd.locationId JOIN campus_classsection cs ON csc.classsection_id_int =cs.classsection_id_int JOIN campus_library_location cll ON cll.library_loc_id=clsd.libraryid  WHERE csc.fms_acadamicyear_id_int like ? AND cll.loc_id like ? AND cd.classdetail_id_int like ? AND cs.classsection_id_int like ? AND (clsd.status LIKE ? OR st.student_fname_var LIKE ? OR csc.student_rollno LIKE ? OR cd.classdetails_name_var LIKE ? OR cs.classsection_name_var LIKE ? OR clsd.subscriberNumber LIKE ? OR st.student_admissionno_var LIKE ?) order by clsd.subscriberNumber ASC";
    public static final String studentlistdetailsbysubName="SELECT DISTINCT clsd.*,CASE WHEN clsd.status='block' THEN 'Blocked' ELSE clsd.status END statuss,CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student,st.student_admissionno_var,CASE WHEN csc.student_rollno='' THEN '-' WHEN csc.student_rollno IS NULL THEN '-' ELSE csc.student_rollno END student_rollno,cd.classdetails_name_var,cs.classsection_name_var FROM campus_library_subscriber_details clsd JOIN campus_student st ON clsd.subscriberId=st.student_id_int JOIN campus_student_classdetails csc ON st.student_id_int=csc.student_id_int JOIN campus_classdetail cd ON csc.classdetail_id_int=cd.classdetail_id_int AND csc.locationId=cd.locationId JOIN campus_classsection cs ON csc.classsection_id_int =cs.classsection_id_int JOIN campus_library_location cll ON cll.library_loc_id=clsd.libraryid  WHERE csc.fms_acadamicyear_id_int like ? AND cll.loc_id like ? AND cd.classdetail_id_int like ? AND cs.classsection_id_int like ? AND (clsd.status LIKE ? OR st.student_fname_var LIKE ? OR csc.student_rollno LIKE ? OR cd.classdetails_name_var LIKE ? OR cs.classsection_name_var LIKE ? OR clsd.subscriberNumber LIKE ? OR st.student_admissionno_var LIKE ?) order by student ASC";
